import "./Station.css"

export default function stations({stationName, currentStation, setStation}) {
   
    const textStyle = {
        backgroundColor: (stationName.toUpperCase() === currentStation) ? "green" : "black"
    }
    return (<button className="station" style={textStyle} onClick={() => {
        setStation(stationName.toUpperCase());
    }}>{stationName}
        </button>);
}
