import "./Station.css"

export default function stations({stationName, stationList, setStation}) {
   
    const textStyle = {
        backgroundColor: (stationName.toUpperCase() === currentStation || (currentStation === null && stationName === "All Stations")) ? "rgb(81, 81, 81)" : "transparent"
        //This text style property makes it so the hover property doesn't work.
    }
    return (<button className="station" style={textStyle} onClick={() => {
        if (stationName !== "All Stations") {
        setStation(stationName.toUpperCase());
        }else {
            setStation(null);
        }
    }}>{stationName}
        </button>);
}
