import "./Station.css"

export default function stations({stationName, stationList, setStation}) {
   
    const textStyle = {
        backgroundColor: (stationList.includes(stationName.toUpperCase()) || ("" in stationList && stationName === "All Stations")) ? "rgb(81, 81, 81)" : "transparent"
        //This text style property makes it so the hover property doesn't work.
    }
    return (<button className="station" style={{textStyle}} onClick={() => {
        let currentStation = stationName;
        if (stationName === "All Stations") {
            currentStation = "";
        }
        
        if (!(stationList.includes(currentStation.toUpperCase()))) {
            setStation([...stationList, stationName.toUpperCase()]); 
            console.log("Not currently in list");
        }else {
            setStation(stationList.filter((station) => (station !== stationName.toUpperCase())));
        }
        console.log(stationList);
        console.log(currentStation.toUpperCase());
        
    }}>{stationName}
        </button>);
}
